var kickback_Time_Container = document.getElementById('kickback-counter-gsweb'); kickback_Time_Container.innerHTML = Kickback_Time; startTimer(); function startTimer() { var presentTime = kickback_Time_Container.innerHTML; if ( presentTime != "00:00" ) { var timeArray = presentTime.split(/[:]+/); var m = parseInt(timeArray[0]); var s = parseInt(timeArray[1]); if ( s == 00 ) { if ( m < 1 ) { clearTimeout(startTimer); } else { m = m - 1 } } var s = checkSecond((timeArray[1] - 1)); kickback_Time_Container.innerHTML = "0" + m + ":" + s; setTimeout(startTimer, 1000); } } function checkSecond(sec) { if ( sec < 10 && sec >= 0 ) { sec = "0" + sec } if ( sec < 0) { sec = "59" } return sec; } 